import { Component } from '@angular/core';
import {AppStateService} from "../services/app-state.service";

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {
 constructor(public appState : AppStateService) {
 }

  totalCheckedProducts() {
     let checkedProducts =
       this.appState.productsState.products.filter((p:any)=>p.checked==true);
     return checkedProducts.length;
  }
}
<div class="p-3">
  <div class="card">
    <div class="card-body">
      <ul class="nav nav-pills">
        <li>
          <label class="border border-info p-1 bg-danger text-white m-1">Pages :{{appState.productsState.totalPages}}</label>
        </li>
        <li>
          <label class="border border-info p-1 bg-info text-white m-1">
            Size :{{appState.productsState.pageSize}}
          </label>
        </li>
        <li>
          <label class="border border-info p-1 bg-success text-white m-1">
            Total :{{appState.productsState.totalProducts}}
          </label>
        </li>
        <li>
          <label class="border border-info p-1 bg-primary text-white m-1">
            Checked :{{totalCheckedProducts()}}
          </label>
        </li>
      </ul>
    </div>
  </div>
</div>
